// Declarations for static assets

// ---- Fonts ----
declare module '*.ttf';
declare module '*.TTF';
declare module '*.otf';
declare module '*.woff';
declare module '*.woff2';

// ---- Images ----
declare module '*.png';
declare module '*.jpg';
declare module '*.jpeg';
declare module '*.gif';
declare module '*.bmp';
declare module '*.webp';
declare module '*.svg';

// ---- Audio ----
declare module '*.mp3';
declare module '*.wav';
declare module '*.ogg';
declare module '*.aac';
declare module '*.m4a';

// ---- Video ----
declare module '*.mp4';
declare module '*.mov';
declare module '*.avi';
declare module '*.webm';

// ---- Animation / JSON ----
declare module '*.json';
declare module '*.lottie.json';

// ---- Misc ----
declare module '*.pdf';
declare module '*.docx';
declare module '*.xlsx';
declare module '*.csv';
declare module '*.txt';
declare module '*.md';
